let angle = 0.5

s.setup= ()=>{ 
  s.createCanvas(500, 500);
  e.push("branch")
  e.push("left")
  console.log(e.val())
  e.pop()
  e.pop()
} 

s.draw= ()=>{ 
  try{

  s.background(250);
  s.translate(s.width/2, s.height);
  e.push("branch")
    branch(100);
  e.pop
  }catch(e){
    console.log(e)
  }

}

function branch(len) {
  s.line(0, 0, 0, -len);
  s.translate(0, -len);
  if (len > 10) {
    s.push();
    e.push("right")
    s.rotate(angle+ (e.val()?? 0));
    branch(len * 0.75)
    e.pop();
    s.pop();

    s.push();
    e.push("left");

    s.rotate(-angle+ (e.val()??0) );
    branch(len * 0.75)

    s.pop();  
    e.pop();
  }
}
